const rawText = $input.first().json.mensagem || ""; // Garante que a string não seja undefined

// 1️⃣ Remover quebras de linha estranhas e normalizar espaços
let cleanedText = rawText.replace(/[\r\n]+/g, " ").trim();

// 2️⃣ Remover múltiplos espaços seguidos
cleanedText = cleanedText.replace(/\s{2,}/g, " ");

// 3️⃣ Corrigir formatações comuns
cleanedText = cleanedText
  .replace(/\[Link\]/g, "") // Remove placeholders "[Link]"
  .replace(/mailto:/g, "") // Remove "mailto:"
  .replace(/tel:/g, "") // Remove "tel:"
  .replace(/•/g, "-") // Substitui bullets por "-"
  .replace(/(\w)\s+-\s+(\w)/g, "$1 - $2") // Ajusta formatação de datas e títulos
  .replace(/https?:\/\/\S+/g, "") // Remove links
  .replace(/\s*,\s*/g, ", ") // Normaliza vírgulas e espaços extras
  .replace(/\s*-\s*/g, " - ") // Ajusta formatação de traços
  .replace(/^\s+|\s+$/g, ""); // Remove espaços extras no início e no fim

// Retorna o JSON com o nome esperado
return { mensagem: cleanedText };
