return [{
  json: {
    message: (() => {
      const mensagens = [
        "Oi! Tudo bem?",
        "Como foi o seu dia?",
        "Que bom te ver por aqui!",
        "Você está bem?",
        "Faz tempo que não nos falamos!",
        "O que tem feito de bom ultimamente?",
        "Tenho uma novidade para te contar!",
        "Vamos combinar de nos encontrar?",
        "Já viu aquele filme novo?",
        "Que tal um café essa semana?",
        "Senti sua falta!",
        "Como estão as coisas no trabalho?",
        "E aí, animado para o fim de semana?",
        "Gostei da sua ideia, vamos conversar mais sobre isso!",
        "Está precisando de ajuda com alguma coisa?",
        "Você viu aquele jogo ontem?",
        "A gente precisa colocar a conversa em dia!",
        "O tempo está tão bom hoje, né?",
        "Fiquei sabendo de uma notícia incrível!",
        "Você tem alguma recomendação de livro ou série?",
        "Vamos planejar uma viagem juntos!",
        "Adoro conversar com você, sempre aprendo algo novo!",
        "Como está sua família?",
        "Qual o seu plano para hoje à noite?",
        "Parece que faz anos que não nos vemos!"
      ];
      const randomIndex = Math.floor(Math.random() * mensagens.length);
      return mensagens[randomIndex];
    })()
  }
}];

